USE dbiykpinec1m8s;

CREATE TABLE sim_active_codes (
    simCode TEXT    NOT NULL,
	jobCode TEXT    NOT NULL
);

CREATE TABLE sim_terminals (
    id          INT     AUTO_INCREMENT,
    slug 		TEXT    NOT NULL,
    jobCode 	TEXT    NOT NULL,
    displayName TEXT,
    access 		INT		NOT NULL,
	state 		TEXT    NOT NULL,
	stateData 	INT,
	PRIMARY KEY (id)
);

CREATE TABLE sim_entries (
    id          INT     AUTO_INCREMENT,
    terminal_id INT     NOT NULL,
    icon        ENUM('files','darkweb','cameras','locks','defenses','utilities')
                        NOT NULL,
    path        TEXT    NOT NULL,
    type        TEXT    NOT NULL,
    access      INT,
    modify      INT,
    title       TEXT,
    contents    TEXT,
    state       TEXT,
    previous   TEXT,
    PRIMARY KEY (id),
    FOREIGN KEY (terminal_id)
        REFERENCES sim_terminals(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE
);

CREATE TABLE users (
    ml_id       INT     NOT NULL UNIQUE,
    userCode    TEXT    NOT NULL,
    charName    TEXT    NOT NULL,
    PRIMARY KEY (ml_id)
);

CREATE TABLE sim_access_logs (
    id          INT     AUTO_INCREMENT,
    terminal_id INT     NOT NULL,
    user_id     INT,
    mask        TEXT,
    reassignee  TEXT,
    state       TEXT    NOT NULL,
    tags        INT,
    PRIMARY KEY (id),
    FOREIGN KEY (terminal_id)
        REFERENCES sim_terminals(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE,
    FOREIGN KEY (user_id)
        REFERENCES users(ml_id)
        ON UPDATE CASCADE
        ON DELETE CASCADE
);

CREATE TABLE cpu_roles (
    id      INT     AUTO_INCREMENT,
    name    TEXT    NOT NULL,
    PRIMARY KEY (id)
);

CREATE TABLE cpu_functions (
    id          INT     AUTO_INCREMENT,
    name        TEXT    NOT NULL,
    type        TEXT,
    hacking_cat TEXT,
    PRIMARY KEY (id)
);

CREATE TABLE cpu_caviats (
    id          INT     AUTO_INCREMENT,
    ml_name     TEXT,
    displayName TEXT,
    PRIMARY KEY (id)
);

CREATE TABLE ml_functions (
    id          INT     AUTO_INCREMENT,
    ml_id       INT     NOT NULL,
    ml_name     TEXT    NOT NULL,
    `rank`      INT     NOT NULL,
    function_id INT     NOT NULL,
    cav_type    TEXT,
    cav_id      INT,
    role_id     INT,
    tier        INT,
    PRIMARY KEY (id),
    FOREIGN KEY (function_id)
        REFERENCES cpu_functions(id)
        ON UPDATE CASCADE
        ON DELETE RESTRICT,
    FOREIGN KEY (role_id)
        REFERENCES cpu_roles(id)
        ON UPDATE CASCADE
        ON DELETE RESTRICT,
    FOREIGN KEY (cav_id)
        REFERENCES cpu_caviats(id)
        ON UPDATE CASCADE
        ON DELETE RESTRICT
);

CREATE TABLE user_functions (
    user_id         INT     NOT NULL,
    mlFunction_id   INT     NOT NULL,
    cav_id          TEXT,
    CONSTRAINT userFunction
        PRIMARY KEY (user_id, mlFunction_id),
    FOREIGN KEY (user_id)
        REFERENCES users(ml_id)
        ON UPDATE CASCADE
        ON DELETE CASCADE,
    FOREIGN KEY (mlFunction_id)
        REFERENCES ml_functions(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE
);
