CREATE TABLE items (
    id          INT     AUTO_INCREMENT,
    name        TEXT    NOT NULL,
    tier        INT     NOT NULL,
    category    TEXT    NOT NULL,
    radio       TEXT,
    abbr        TEXT    NOT NULL,
    enabled     BOOL    NOT NULL,
    PRIMARY KEY (id)
);

CREATE TABLE item_effects (
    id          INT     AUTO_INCREMENT,
    item_id     INT     NOT NULL,
    abbr        TEXT,
    use_loc     TEXT    NOT NULL,
    req_type    TEXT,
    requirement TEXT,
    charges     INT,
    per_type    TEXT,
    notes       TEXT,
    PRIMARY KEY (id),
    FOREIGN KEY (item_id)
        REFERENCES items(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE
);

CREATE TABLE user_items (
    user_id INT NOT NULL,
    item_id INT NOT NULL,
    count   INT,
    FOREIGN KEY (user_id)
        REFERENCES users(ml_id)
        ON UPDATE CASCADE
        ON DELETE CASCADE,
    FOREIGN KEY (item_id)
        REFERENCES items(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE
);

CREATE TABLE item_uses (
    user_id     INT     NOT NULL,
    effect_id   INT     NOT NULL,
    simCode     TEXT    NOT NULL,
    jobCode     TEXT    NOT NULL,
    terminal_id INT     NOT NULL,
    FOREIGN KEY (user_id)
        REFERENCES users(ml_id)
        ON UPDATE CASCADE
        ON DELETE CASCADE,
    FOREIGN KEY (effect_id)
        REFERENCES item_effects(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE,
    FOREIGN KEY (terminal_id)
        REFERENCES sim_terminals(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE
);

CREATE TABLE sim_user_actions (
    id          INT         AUTO_INCREMENT,
    time        TIMESTAMP   NOT NULL,
    user_id     INT         NOT NULL,
    target_type TEXT        NOT NULL,
    target_id   INT         NOT NULL,
    action      TEXT        NOT NULL,
    newState    TEXT        NOT NULL,
    cost        INT         NOT NULL,
    global      BOOLEAN     NOT NULL,
    PRIMARY KEY (id),
    FOREIGN KEY (user_id)
        REFERENCES users(ml_id)
        ON UPDATE CASCADE
        ON DELETE CASCADE
);

