-- CREATE DATABASE cpu_term;

CREATE TABLE activeJob (
    simCode TEXT    NOT NULL,
	jobCode TEXT    NOT NULL
);

CREATE TABLE terminals (
    id          INT     AUTO_INCREMENT,
    slug 		TEXT    NOT NULL,
    jobCode 	TEXT    NOT NULL,
    displayName TEXT,
    access 		INT		NOT NULL,
	state 		TEXT    NOT NULL,
	stateData 	INT,
	PRIMARY KEY (id)
);

CREATE TABLE entries (
    id          INT     AUTO_INCREMENT,
    terminal_id INT     NOT NULL,
    icon        ENUM('files','darkweb','cameras','locks','defenses','utilities')
                        NOT NULL,
    path        TEXT    NOT NULL,
    type        TEXT    NOT NULL,
    access      INT,
    modify      INT,
    title       TEXT,
    contents    TEXT,
    state       TEXT,
    PRIMARY KEY (id),
    FOREIGN KEY (terminal_id)
        REFERENCES terminals(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE
);

CREATE TABLE users (
    id          INT     AUTO_INCREMENT,
    userCode    TEXT    NOT NULL,
    charName    TEXT    NOT NULL,
    PRIMARY KEY (id)
);

CREATE TABLE accessLogs (
    id          INT     AUTO_INCREMENT,
    terminal_id INT     NOT NULL,
    user_id     INT,
    mask        TEXT,
    reassignee  TEXT,
    state       TEXT    NOT NULL,
    PRIMARY KEY (id),
    FOREIGN KEY (terminal_id)
        REFERENCES terminals(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE,
    FOREIGN KEY (user_id)
        REFERENCES users(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE
);

CREATE TABLE roles (
    id      INT     AUTO_INCREMENT,
    name    TEXT    NOT NULL,
    PRIMARY KEY (id)
);

CREATE TABLE functions (
    id          INT     AUTO_INCREMENT,
    name        TEXT    NOT NULL,
    type        TEXT,
    hacking_cat TEXT,
    PRIMARY KEY (id)
);

CREATE TABLE ml_functions (
    id          INT     AUTO_INCREMENT,
    ml_id       INT     NOT NULL,
    ml_name     TEXT    NOT NULL,
    `rank`      INT     NOT NULL,
    function_id INT     NOT NULL,
    caviat      TEXT,
    role_id     INT,
    PRIMARY KEY (id),
    FOREIGN KEY (function_id)
        REFERENCES functions(id)
        ON UPDATE CASCADE
        ON DELETE RESTRICT,
    FOREIGN KEY (role_id)
        REFERENCES roles(id)
        ON UPDATE CASCADE
        ON DELETE RESTRICT
);

CREATE TABLE user_functions (
    user_id         INT NOT NULL,
    mlFunction_id   INT NOT NULL,
    CONSTRAINT userFunction
        PRIMARY KEY (user_id, mlFunction_id),
    FOREIGN KEY (user_id)
        REFERENCES users(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE,
    FOREIGN KEY (mlFunction_id)
        REFERENCES ml_functions(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE
);

CREATE TABLE items (
    id      INT     AUTO_INCREMENT,
    name    TEXT    NOT NULL,
    tier    INT,
    type    TEXT    NOT NULL,
    radio   TEXT,
    PRIMARY KEY (id)
);

CREATE TABLE item_effects (
    id          INT AUTO_INCREMENT,
    item_id     INT NOT NULL,
    charges     INT,
    per_type    TEXT,
    use_loc     TEXT,
    effect      TEXT,
    PRIMARY KEY (id),
    FOREIGN KEY (item_id)
        REFERENCES items(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE
);

CREATE TABLE user_items (
    user_id INT NOT NULL,
    item_id INT NOT NULL,
    FOREIGN KEY (user_id)
        REFERENCES users(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE,
    FOREIGN KEY (item_id)
        REFERENCES items(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE
);

CREATE TABLE item_uses (
    user_id     INT     NOT NULL,
    effect_id   INT     NOT NULL,
    simCode     TEXT    NOT NULL,
    jobCode     TEXT    NOT NULL,
    FOREIGN KEY (user_id)
        REFERENCES users(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE,
    FOREIGN KEY (effect_id)
        REFERENCES item_effects(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE
);

